from textblob import TextBlob
import nltk
from nltk.tokenize import sent_tokenize, word_tokenize
from nltk.corpus import stopwords
import matplotlib.pyplot as plt
import networkx as nx

# Make sure to download necessary nltk data
nltk.download('punkt')
nltk.download('stopwords')


import docx
from docx.shared import Inches
import numpy as np
# Define the competitors
# competitors = ['ryzen', 'amd', 'nvidia', 'radeon']

competitors = ['ryzen', 'amd', 'nvidia']

# Define a function to extract sentences containing competitors
def extract_competitor_sentences(text, competitors):
    sentences = sent_tokenize(text)
    competitor_sentences = []
    for i, sentence in enumerate(sentences):
        for competitor in competitors:
            if competitor.lower() in sentence.lower():
                if i > 0:  # Check if there is a previous sentence
                    previous_sentence = sentences[i-1]
                    competitor_sentences.append((previous_sentence, sentence, competitor))
                break
    return competitor_sentences

# Define a function to calculate sentiment of a text
def calculate_sentiment(text):
    stop_words = set(stopwords.words('english'))
    words = word_tokenize(text.lower())
    words = [word for word in words if word.isalnum() and word not in stop_words]
    cleaned_text = ' '.join(words)
    blob = TextBlob(cleaned_text)
    sentiment_score = blob.sentiment.polarity
    
    # Classify sentiment
    if sentiment_score > 0:
        sentiment_class = 'Positive'
    elif sentiment_score < 0:
        sentiment_class = 'Negative'
    else:
        sentiment_class = 'Neutral'
        
    return sentiment_score, sentiment_class

# Define a function to generate sentiment scores from reviews
def generate_sentiment_scores(data, competitors):
    sentiment_scores = {competitor: [] for competitor in competitors}
    for item in data:
        text = item['text']
        competitor_sentences = extract_competitor_sentences(text, competitors)
        for previous_sentence, sentence, competitor in competitor_sentences:
            sentence_sentiment_score, _ = calculate_sentiment(sentence)
            sentiment_scores[competitor].append(sentence_sentiment_score)
    return sentiment_scores

# Apply the function to the dataset
def generate_sentiment_analysis_doc(data, competitors):
    doc = docx.Document()
    for item in data:
        text = item['text']
        competitor_sentences = extract_competitor_sentences(text, competitors)
        for previous_sentence, sentence, competitor in competitor_sentences:
            # Calculate sentiment of the entire review
            review_sentiment_score, review_sentiment_class = calculate_sentiment(text)
            
            # Calculate sentiment of the sentence containing the competitor
            sentence_sentiment_score, sentence_sentiment_class = calculate_sentiment(sentence)
            
            doc.add_paragraph(f"Competitor: {competitor}")
            doc.add_paragraph(f"Previous Sentence: {previous_sentence}")
            doc.add_paragraph(f"Sentence: {sentence}")
            doc.add_paragraph(f"Sentence Sentiment: {sentence_sentiment_class} ({sentence_sentiment_score})")
            doc.add_paragraph(f"Review Sentiment: {review_sentiment_class} ({review_sentiment_score})\n")
    
    # Save the Word document
    doc.save('sentiment_analysis_1.docx')

